import {makeStyles} from "@mui/styles";


const useStyles = makeStyles({
    imageContainer:{
        cursor:"pointer"
    },
    ImagesMiddle:{
        marginTop:'-60px !important'
    },
    ImageHovered:{
        transform:"scale(1.3)",
        transition:"3"
    }
});
export default useStyles;
