import { Button, Container, Grid } from "@mui/material";
import React, { useEffect, useRef, useState } from "react";
import useStyles from "./profile.style";
import { ResumePicture } from "../../ResumeData/ResumeData";
import {BackspaceOutlined} from "@mui/icons-material";



function Profile() {
    const styles = useStyles();
    const profileBox = React.createRef();
    const profileText = React.createRef();
    const [test, updateTest] = useState(0);

    useEffect((profileBox =profileBox  , profileText =profileText ) => {
        profileText.current.setAttribute('style', 'width: ' + profileBox.current.clientWidth + 'px;');
        window.onresize = function(){ profileText.current.setAttribute('style', 'width: ' + profileBox.current.clientWidth + 'px;');}


    }, [])

    return (

        <Grid className={styles.profileGrid} container spacing={2}>

            <Grid className={styles.profile} item xs={4} ref={profileBox}>
                <div className={styles.profilePicture}>

                    <img className={styles.profilePic + ' img-grid'} src={ResumePicture} alt='sdasd' />
                </div>

                <div className={styles.profileText} ref={profileText} >


                    <h3>Hi, I'm Jhon Smith</h3>
                    <h1 className="text-red">
                        Creative Designer
                    </h1>
                    <p className="text">Web, Mobile App UI/UX </p>
                    <br />
                    <br />
                    <div className={'f-row ' + styles.profileButtons}>
                        <Button>Download CV</Button>
                        <Button>Hire Me !</Button>
                        <BackspaceOutlined style={{cursor:'pointer'}} onClick={() => alert('I like FireFox new user interface')} />
                    </div>

                </div>
                <div className={styles.profileSVG}>
                    <svg xmlns="http://www.w3.org/2000/svg" width="578.174" height="480.246" viewBox="0 0 578.174 480.246">
                        <path id="Path_1" data-name="Path 1" d="M371.868,679.677s32.186-142.084,136.8-80.3,235.509-35.266,299.885-95.467,141.492,31.482,141.492,31.482V963.676H371.868Z" transform="translate(-371.868 -483.431)" fill="#222b45" />
                    </svg>

                </div>



            </Grid>


        </Grid>
    );
}

export default Profile;
