import {makeStyles} from "@mui/styles";

const useStyles = makeStyles({
    JobsContainer: {
        display: 'flex',
        justifyContent: "center",
        // backgroundImage :"linear-gradient(#fd8590 , #fc364a)",
        borderWidth: ' 8px',
        borderStyle: 'solid',
        borderImage: 'linear-gradient(#fd8590 , #fc364a)',
        borderImageSlice: 1,
        borderRight: 'none',
        borderBottom: 'none',
        borderTop: 'none',

    },
    erwr: {
        backgroundImage: "linear-gradient(#fd8590 , #fc364a)"
    }
});
export default useStyles;
