import {makeStyles} from "@mui/styles";


const useStyles = makeStyles({
    imageContainer:{
        cursor:"pointer",
        '&> img':{
            transform:"scale(1)",
        }
    },
    ImagesMiddle:{
        marginTop:'-60px !important'
    },
    ImageHovered:{
        transform:"scale(1.3) !important",
        transition: 'transform 1.2s ease-in-out'
    }
});
export default useStyles;
