import {makeStyles} from "@mui/styles";

const useStyles = makeStyles({
    JobsContainer: {
        display:'flex',
        justifyContent:"center",
        backgroundImage :"linear-gradient(#fd8590 , #fc364a)",
        borderLeft : "linear-gradient(#fd8590 , #fc364a) 1px solid"

    },
    erwr:{
      backgroundImage :"linear-gradient(#fd8590 , #fc364a)"
    }
});
export default useStyles;
