import {makeStyles} from "@mui/styles";

function useStyles (is_project) {


    console.log(is_project)

    makeStyles({

    JobsContainer: {
        display: 'flex',
        justifyContent: "center",
        // backgroundImage :"linear-gradient(#fd8590 , #fc364a)",
        borderWidth: '0 0 0 15px',
        borderStyle: 'solid',
        borderImage: 'linear-gradient(#fd8590 , #fc364a)',
        borderImageSlice: 1,
        paddingLeft: "10px",
        boxShadow: '0px 0px 20px rgba(0, 0, 0, 0.2)',

    },
    JobsGradiantBorder: {
        borderImage: 'linear-gradient(#fd8590 , #fc364a)',

    },
    ProjectsGradiantBorder: {
        borderImage: 'linear-gradient(#b281fd , #844cfa)',

    },
    JobTitle: {
        fontWeight: 'bold',

    }
})}

export default useStyles;
